sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // Get the current date and month
    var today = new GlideDate(); // Get current date
    var currentMonth = today.getMonth(); // Get the current month (0 = January, 6 = July, 9 = October)

    // Term1 logic: July (6 = July)
    if (current.u_boolean_1 == true && (currentMonth == 6)) {
        updatePaymentStatus('Paid'); // Term1 is true and within July -> Paid
    } else if (current.u_boolean_1 == false && (currentMonth == 6)) {
        updatePaymentStatus('Overdue'); // Term1 is false and within July -> Overdue
    }

    // Term2 logic: October (9 = October)
    if (current.u_boolean_2 == true && (currentMonth == 9)) {
        updatePaymentStatus('Paid'); // Term2 is true and within October -> Paid
    } else if (current.u_boolean_2 == false && (currentMonth == 9)) {
        updatePaymentStatus('Overdue'); // Term2 is false and within October -> Overdue
    }

    // Term3 logic: January (0 = January)
    if (current.u_boolean_4 == true && (currentMonth == 0)) {
        updatePaymentStatus('Paid'); // Term3 is true and within January -> Paid
    } else if (current.u_boolean_4 == false && (currentMonth == 0)) {
        updatePaymentStatus('Overdue'); // Term3 is false and within January -> Overdue
    }

    // Function to update Payment Status
    function updatePaymentStatus(status) {
        current.payment_status = status; // Set the payment status to either "Paid" or "Overdue"
    }

})(current, previous);
