sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    // Check if the approval status is 'Rejected'
    var studentNumber = current.getDisplayValue('document_id');
    gs.addInfoMessage(studentNumber);

    var approverComments = current.comments;
    // gs.info("Commentsbusinessrule 1: " + approverComments + ", Document ID: " + studentNumber);

    function extractStudentRequestNumber(str) {
        return str.split(':')[1].trim();
    }

    var request = studentNumber;
    var extractedNumber = extractStudentRequestNumber(request);
    // gs.info("Extracted Number: " + extractedNumber);

    var studentRecord = new GlideRecord('x_1210081_school_0_student_request');
    studentRecord.addQuery('number', extractedNumber);
    studentRecord.query();

    if (studentRecord.next()) {
        // gs.info("Found record with number: " + extractedNumber);
        studentRecord.u_string_1 = approverComments;
        var updateResult = studentRecord.update(); // Save the changes
        if (updateResult) {
            gs.info("Record updated successfully");
        } else {
            gs.info("Record update failed");
        }
    } else {
        gs.info("No record found with number: " + extractedNumber);
    }
})(current, previous);
