sys_script_client.script:
function onLoad() {
    var grade = g_form.getValue('grade').toLowerCase();  // Convert to lowercase to avoid case sensitivity

    // Nursery, LKG, UKG
    if (grade === 'nursary' || grade === 'lkg' || grade === 'ukg') {
        g_form.setValue('u_integer_1', '120000');
        g_form.setValue('bus_fee', '18000');
        g_form.setValue('uniform_fee', '1200');
        g_form.setValue('books_fee', '800');

    // 1st class to 5th class
    } else if (grade === '1st class' || grade === '2nd class' || 
               grade === '3rd class' || grade === '4th class' || 
               grade === '5th class') {
        g_form.setValue('u_integer_1', '150000');
        g_form.setValue('bus_fee', '18000');
        g_form.setValue('uniform_fee', '2000');
        g_form.setValue('books_fee', '1500');

    // 6th class to 10th class
    } else if (grade === '6th class' || grade === '7th class' || 
               grade === '8th class' || grade === '9th class' || 
               grade === '10th class') {
        g_form.setValue('u_integer_1', '180000');
        g_form.setValue('bus_fee', '18000');
        g_form.setValue('uniform_fee', '4000');
        g_form.setValue('books_fee', '3500');

    // Clear fields if grade doesn't match any of the above
    } else {
        g_form.clearValue('u_integer_1');
        g_form.clearValue('bus_fee');
        g_form.clearValue('uniform_fee');
        g_form.clearValue('books_fee');
    }
}
