sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   // Get reference record of admission number
   g_form.getReference('admission_no', function(ve) {
      if (ve) {
         // Set values for the respective fields from the referenced record 
         g_form.setValue('student_name', ve.student_name);
         g_form.setValue('grade', ve.grade);
         g_form.setValue('gender', ve.gender);
         g_form.setValue('gaurdian_name', ve.gaurdian_name);
         g_form.setValue('mobile_number', ve.mobile_number); 
         g_form.setValue('email', ve.email); // New email field

         // Disable fields to prevent further editing
         g_form.setDisabled('student_name', true);
         g_form.setDisabled('grade', true);
         g_form.setDisabled('gender', true);
         g_form.setDisabled('gaurdian_name', true);
         g_form.setDisabled('mobile_number', true); 
         g_form.setDisabled('email', true); // Disable email field
      }
   });
}
