sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    // Check if the form is loading or if newValue is empty
    if (isLoading || newValue === '') {
        return;
    }

    var test = g_form.getValue("tuition_fee_term_3");
    var number = parseInt(test.replace(/,/g, ''), 10);
    // Retrieve the fee values and the boolean
    var tuitionFee3 = g_form.getValue("tuition_fee_term_3");
    var busFee3 = g_form.getValue("bus_fee_term_3");
    var totalDueFee = g_form.getValue("due_fee_for_the_year");

    tuitionFee3 = parseInt(tuitionFee3.replace(/,/g, ''), 10);
    busFee3 = parseInt(busFee3.replace(/,/g, ''), 10);
    totalDueFee = parseInt(totalDueFee.replace(/,/g, ''), 10);





    var term3check = g_form.getValue("u_boolean_4");



    // Check if term1check is "true"
    if (term3check === "true") {
        var term3 = tuitionFee3 + busFee3;
        var final = totalDueFee - term3;
        g_form.setValue("due_fee_for_the_year", final);
    } else {
        var term4 = tuitionFee3 + busFee3;
        var final4 = totalDueFee + term4;
        g_form.setValue("due_fee_for_the_year", final4);
    }

    // Optionally save the form
    g_form.save();
}
