sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
	var due = g_form.getValue('paymentstatus1')
    if ( due!= 'overdue') {
        // Get the due amount and ensure it's a number
        var dueAmount1 = parseFloat(g_form.getValue("dueamount1"));


        // Check if the due amount is 0 and update payment status accordingly
        if (dueAmount1 === 0) {
            g_form.setValue("paymentstatus1", "paid");

        } else if (dueAmount1 > 0) {
            alert("paymentstatus1");
            g_form.setValue("paymentstatus1", "pending");

        }
    }
}
