sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }


   var y = parseInt(g_form.getValue("average")) || 0;

   var distinctionThreshold = 85;  // Set the threshold for distinction
   var passThreshold = 35;         // Set the threshold for pass


   if (y >= distinctionThreshold) {
      g_form.setValue("result", "distinction");
      g_form.setValue("section", "a section"); 
   } else if (y >= passThreshold) {
      g_form.setValue("result", "pass");
      g_form.setValue("section", "b section"); 
   } else {
      g_form.setValue("result", "fail");
      g_form.setValue("section", "c section");  
   }


}
