sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

// alert(g_form.getValue("tuition_fee_term_1"));
// alert(g_form.getValue("payingamount1"));

   var tuitionFee3 = g_form.getValue("tuition_fee_term_3");
   var busFee3 = g_form.getValue("bus_fee_term_3");
   var payingAmount3 = g_form.getValue("payingamount3");

      tuitionFee3 = parseInt(tuitionFee3.replace(/,/g, ''), 10);
    busFee3 = parseInt(busFee3.replace(/,/g, ''), 10);
    payingAmount3 = parseInt(payingAmount3.replace(/,/g, ''), 10);

   var term3 = tuitionFee3 + busFee3;
   var dueAmount = term3 - payingAmount3;


   g_form.setValue("dueamount3", dueAmount);
   g_form.save();
}
