sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    // Check if the form is loading or if newValue is empty
    if (isLoading || newValue === '') {
        return;
    }

    var test = g_form.getValue("tuition_fee_term_2");
    var number = parseInt(test.replace(/,/g, ''), 10);
    // Retrieve the fee values and the boolean
    var tuitionFee2 = g_form.getValue("tuition_fee_term_2");
    var busFee2 = g_form.getValue("bus_fee_term_2");
    var totalDueFee = g_form.getValue("due_fee_for_the_year");

    tuitionFee2 = parseInt(tuitionFee2.replace(/,/g, ''), 10);
    busFee2 = parseInt(busFee2.replace(/,/g, ''), 10);
    totalDueFee = parseInt(totalDueFee.replace(/,/g, ''), 10);





    var term2check = g_form.getValue("u_boolean_2");



    // Check if term1check is "true"
    if (term2check === "true") {
        var term2 = tuitionFee2 + busFee2;
        var final = totalDueFee - term2;
        g_form.setValue("due_fee_for_the_year", final);
    } else {
        var term3 = tuitionFee2 + busFee2;
        var final3 = totalDueFee + term3;
        g_form.setValue("due_fee_for_the_year", final3);
    }

    // Optionally save the form
    g_form.save();
}
