sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    // Check if the form is loading or if newValue is empty
    if (isLoading || newValue === '') {
        return;
    }

    var test = g_form.getValue("tuition_fee_term_1");
    var number = parseInt(test.replace(/,/g, ''), 10);
    // Retrieve the fee values and the boolean
    var tuitionFee1 = g_form.getValue("tuition_fee_term_1");
    var busFee1 = g_form.getValue("bus_fee_term_1");
    var totalDueFee = g_form.getValue("due_fee_for_the_year");

    tuitionFee1 = parseInt(tuitionFee1.replace(/,/g, ''), 10);
    busFee1 = parseInt(busFee1.replace(/,/g, ''), 10);
    totalDueFee = parseInt(totalDueFee.replace(/,/g, ''), 10);





    var term1check = g_form.getValue("u_boolean_1");



    // Check if term1check is "true"
    if (term1check === "true") {
        var term1 = tuitionFee1 + busFee1;
        var final = totalDueFee - term1;
        g_form.setValue("due_fee_for_the_year", final);
    } else {
        var term2 = tuitionFee1 + busFee1;
        var final2 = totalDueFee + term2;
        g_form.setValue("due_fee_for_the_year", final2);
    }

    // Optionally save the form
    g_form.save();
}
