sys_script_client.script:
function onLoad() {
    var tuitionFee = g_form.getValue('u_integer_1');
    var busFee = g_form.getValue('bus_fee');

    var Paid1 = g_form.getValue('u_boolean_1');
    var Paid2 = g_form.getValue('u_boolean_2');
    var Paid3 = g_form.getValue('u_boolean_4');

    var tuitionAmount = parseFloat(tuitionFee);
    var busAmount = parseFloat(busFee);

    if (!isNaN(tuitionAmount) && !isNaN(busAmount)) {
        // Ensure no decimal points by using Math.floor or Math.round
        var termTuitionFee = Math.round(tuitionAmount / 3);
        var termBusFee = Math.round(busAmount / 3);

        g_form.setValue('tuition_fee_term_1', termTuitionFee);
        g_form.setValue('tuition_fee_term_2', termTuitionFee);
        g_form.setValue('tuition_fee_term_3', termTuitionFee);

        g_form.setValue('bus_fee_term_1', termBusFee);
        g_form.setValue('bus_fee_term_2', termBusFee);
        g_form.setValue('bus_fee_term_3', termBusFee);

        var totalFeeForYear = tuitionAmount + busAmount;

        g_form.setValue('total_fee_for_the_year', totalFeeForYear);
		g_form.setValue('due_fee_for_the_year', totalFeeForYear);
    } else {
        g_form.clearValue('tuition_fee_term_1');
        g_form.clearValue('tuition_fee_term_2');
        g_form.clearValue('tuition_fee_term_3');

        g_form.clearValue('bus_fee_term_1');
        g_form.clearValue('bus_fee_term_2');
        g_form.clearValue('bus_fee_term_3');

        g_form.clearValue('total_fee_for_the_year');
    }
}
