sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    // Retrieve values and ensure they are parsed as integers (or default to 0 if empty)
    var tel = parseInt(g_form.getValue("telugu1")) || 0;
    var hin = parseInt(g_form.getValue("hindi")) || 0;
    var eng = parseInt(g_form.getValue("u_integer_3")) || 0;
    var math = parseInt(g_form.getValue("maths")) || 0;
    var sci = parseInt(g_form.getValue("science")) || 0;
    var soc = parseInt(g_form.getValue("social")) || 0;

    // Calculate the total
    var tl = tel + hin + eng + math + sci + soc;

    // Set the total in the 'total' field
    g_form.setValue("total", tl);
}
