sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

// alert(g_form.getValue("tuition_fee_term_1"));
// alert(g_form.getValue("payingamount1"));

   var tuitionFee2 = g_form.getValue("tuition_fee_term_2");
   var busFee2 = g_form.getValue("bus_fee_term_2");
   var payingAmount2 = g_form.getValue("payingamount2");

      tuitionFee2 = parseInt(tuitionFee2.replace(/,/g, ''), 10);
    busFee2 = parseInt(busFee2.replace(/,/g, ''), 10);
    payingAmount2 = parseInt(payingAmount2.replace(/,/g, ''), 10);

   var term2 = tuitionFee2 + busFee2;
   var dueAmount = term2 - payingAmount2;


   g_form.setValue("dueamount2", dueAmount);
   g_form.save();
}
