sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    var grade = current.grade.toLowerCase();
    if (grade === 'nursary' || grade === 'lkg' || grade === 'ukg') {
        current.u_integer_1 = 120000;
        current.bus_fee = 18000;
        current.uniform_fee = 1200;
        current.books_fee = 800;


    } else if (grade === '1st class' || grade === '2nd class' ||
        grade === '3rd class' || grade === '4th class' ||
        grade === '5th class') {
        current.u_integer_1 = 150000;
        current.bus_fee = 18000;
        current.uniform_fee = 2000;
        current.books_fee = 1500;

    } else if (grade === '6th class' || grade === '7th class' ||
        grade === '8th class' || grade === '9th class' ||
        grade === '10th class') {
        current.u_integer_1 = 180000;
        current.bus_fee = 18000;
        current.uniform_fee = 4000;
        current.books_fee = 3500;


    } else {
        current.u_integer_1 = '';
        current.bus_fee = '';
        current.uniform_fee = '';
        current.books_fee = '';
    }

var termT1 =(Math.round(current.u_integer_1))/3;

var termB1 =(Math.round(current.bus_fee))/3;

var termT2 =(Math.round(current.u_integer_1))/3;

var termB2 =(Math.round(current.bus_fee))/3;
var termT3 =(Math.round(current.u_integer_1))/3;

var termB3 =(Math.round(current.bus_fee))/3;



current.tuition_fee_term_1=termT1;
current.bus_fee_term_1=termB1;
current.tuition_fee_term_2=termT2;
current.bus_fee_term_2=termB2;
current.tuition_fee_term_3=termT3;
current.bus_fee_term_3=termB3;

current.total_fee_for_the_year=termT1+termT2+termT3+termB1+termB2+termB3;
current.due_fee_for_the_year=termT1+termT2+termT3+termB1+termB2+termB3;

})(current, previous);
